let llumOff=document.getElementById("aLesFosques"),llumOn=document.getElementById("aLesClares");function aLesFosques(){document.querySelector("body").style.background="#000",document.querySelector("h1").style.color="#000",document.querySelector(".nad-contSwitchImg").style.backgroundImage="url(img/SwitchOff.png)",document.querySelector("#lletresDeLlum").style.display="block"}function aLesClares(){document.querySelector("body").style.background="#FFF",document.querySelector("h1").style.color="#888",document.querySelector(".nad-contSwitchImg").style.backgroundImage="url(img/SwitchOn.png)",document.querySelector("#lletresDeLlum").style.display="none"}llumOff.addEventListener("click",aLesFosques),llumOn.addEventListener("click",aLesClares);